$(document).ready(function(){$(".sliding-link").click(function(e){e.preventDefault();var o=$(this).attr("href");$("html,body").animate({scrollTop:$(o).offset().top},"slow")}),$(".feedback").bxSlider({pagerCustom:"#feedback-pager",controls:!1,onSliderLoad:function(){$(".feedback").removeClass("feedback-loading")}})});